package myfirst;

public class VariableSwap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x =10;
		int y= 25;
		int temp;
		
		//to swap the values of variable x to  variable y
		temp = x;
		x =y;
		y = temp;
		System.out.println(x);
		System.out.println(y);

	}

}
